#!/bin/sh

rrdtool graph powermon.png --end now --start end-4h DEF:min=powermon.rrd:load:MIN DEF:max=powermon.rrd:load:MAX DEF:avg=powermon.rrd:load:AVERAGE --color SHADEA#425466 --color SHADEB#425466 --color GRID#ffffff7f --color MGRID#FF99007f --color BACK#22374C --color FRAME#000000 --color FONT#ffffff --color CANVAS#22374C --color ARROW#FFFFFF -w 640 -h 150 -t "spacestation power consumption" -v "Watt" 'COMMENT:Watt used within the last 4 hours' 'AREA:avg#000000:Average' 'LINE1:min#46B6EE:Min' 'LINE1:max#FF9900:Max' 2>&1 | grep -v '^737x223$'
rrdtool graph powermon-long.png --end now --start end-3d DEF:min=powermon.rrd:load:MIN DEF:max=powermon.rrd:load:MAX DEF:avg=powermon.rrd:load:AVERAGE --color SHADEA#425466 --color SHADEB#425466 --color GRID#ffffff7f --color MGRID#FF99007f --color BACK#22374C --color FRAME#000000 --color FONT#ffffff --color CANVAS#22374C --color ARROW#FFFFFF -w 640 -h 150 -t "spacestation power consumption" -v "Watt" 'COMMENT:Watt used within the last 4 hours' 'AREA:avg#000000:Average' 'LINE1:min#46B6EE:Min' 'LINE1:max#FF9900:Max' 2>&1 | grep -v '^737x223$'
